<form class="form-signin" action="" method="POST">    <div class="form-signin-heading text-center">        <h1 class="sign-title">Sign In</h1>        <!-- put logo here -->         <img src="<?php echo base_url(); ?>/assets/front/img/logo.png" alt="Admin Login"/>    </div>    <?php    $msg = "";    if ($this->session->flashdata('succ')) {        $class = "alert-danger-suc";        $msg .= $this->session->flashdata('succ');    } elseif ($this->session->flashdata('err')) {        $class = "alert-danger";        $msg .= $this->session->flashdata('err');    } else {        $class = "alert-danger";        $msg .= validation_errors('<h5>', '</h5>');    }    if ($this->session->flashdata('counter_err')) {        $class = "alert-danger";        $msg .= $this->session->flashdata('counter_err');    }    if ($msg != "") {        ?>        <div class="alert alert-block <?php echo $class; ?> fade in">            <button data-dismiss="alert" class="close close-sm" type="button">                <i class="fa fa-times"></i>            </button>    <?php echo $msg; ?>        </div><?php } ?>    <div class="login-wrap">        <input type="text" name="email" id="email" class="form-control" placeholder="Email" value="<?php if (get_cookie('les_rem_email') != '')             {            echo get_cookie('les_rem_email');            }             else if ($this->session->flashdata('posted_email')) {echo $this->session->flashdata('posted_email');} ?>" />        <input type="password" name="password" id="email" class="form-control" placeholder="Password" value="<?php echo (get_cookie('les_rem_password') != '') ? get_cookie('les_rem_password') : set_value('password'); ?>" />        <button class="btn btn-lg btn-login btn-block" type="submit">            <i class="fa fa-check"></i>        </button>		        <label class="checkbox">            <?php            if (get_cookie('les_rem_email', 'les_rem_password') != "") {                $check = 'checked="checked"';            } else {                $check = "";            }            ?>            <input type="checkbox" name="remember_me" id="remember" style="float: left;" <?php echo $check; ?>/>Remember me             <span class="pull-right">                <a data-toggle="modal" href="#myModal"> Forgot Password?</a>            </span>        </label>    </div>    <!-- Modal -->    <div aria-hidden="true" aria-labelledby="myModalLabel" role="dialog" tabindex="-1" id="myModal" class="modal fade">        <div class="modal-dialog">            <div class="modal-content">                <div class="modal-header">                    <button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>                    <h4 class="modal-title">Forgot Password ?</h4>                </div>                <div class="modal-body">                    <span  style="display:none;"></span>                    <p id="ret_t">Enter your e-mail address below to reset your password.</p>                    <p id="not_t"></p>                    <p id="succ_t"></p>                    <input type="text" name="member_email" id="member_email" placeholder="Email" autocomplete="off" class="form-control placeholder-no-fix">                </div>                <div class="modal-footer">                    <button data-dismiss="modal" class="btn btn-default" type="button">Cancel</button>                    <button class="btn btn-primary forgot" type="button">Submit</button>                </div>            </div>        </div>    </div>    <!-- modal --></form><script type="text/javascript">    $(document).ready(function () {        $(".forgot").click(function () {            var Email = $('#member_email').val();            if (Email == "")            {                var html = "<span id='ret_t' class='error1'>Please enter your Email Id</span>";                $("#ret_t").html(html);                $("#ret_t").show();                return false;            }            else            {                var dataString = Email;                //alert(datastring);                $.ajax({                    url: "<?php echo base_url(); ?>admin/resetPassword",                    data: {email: dataString},                    type: "POST",                    cache: false,                    async: false,                    success: function (data)                    {                        if (data == '0')                        {                            var html1 = "<span id='not_t' class='error1'>Entered e-mail address is not registered</span>";                            $("#not_t").html(html1);                            $("#not_t").show();                        }                        else if (data == '1')                        {                            var html2 = "<span id='succ_t' class='error1'>Password has successfully sent to your email-address</span>";                            $("#succ_t").html(html2);                            $("#succ_t").show();                        }                    }                });            }        });    });</script>